import './App.css';
import { useState } from 'react';

function App() {
  const [events, setEvents] = useState([
    { title: "mario's birthday bash", id: 1 },
    { title: "bowser's live stream", id: 2 },
    { title: 'race on moo moo farm', id: 3 },
  ]);

  const handleClick = (id) => {
    setEvents(
      events.filter((e) => {
        return id !== e.id;
      })
    );
    console.log(id);
  };

  return (
    <div className='App'>
      {events.map((event, index) => (
        <div key={event.id}>
          <h2>
            {index} - {event.title}
          </h2>
          {/* using an annonymous function prevents the event from running as soon as the browser loads, which doesn't wait for the click, now it will only work when the button is clicked */}
          <button
            onClick={() => {
              handleClick(event.id);
            }}
          >
            Delete event
          </button>
        </div>
      ))}
    </div>
  );
}

export default App;
